import React, { useState, useEffect, useRef } from 'react';
import ProgressBar from 'progressbar.js';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const splashTextRef = useRef(null);

  useEffect(() => {
    const bar = new ProgressBar.Line(splashTextRef.current, {
      easing: 'easeInOut',
      duration: 1000,
      strokeWidth: 0.2,
      color: '#555',
      trailWidth: 0.2,
      trailColor: '#bbb',
      text: {
        style: {
          position: 'absolute',
          left: '50%',
          top: '50%',
          padding: '0',
          margin: '-30px 0 0 0',
          transform: 'translate(-50%,-50%)',
          fontSize: '1rem',
          color: '#fff',
        },
        autoStyleContainer: false,
      },
      step: (state, bar) => {
        bar.setText(Math.round(bar.value() * 100) + ' %');
      },
    });

    bar.animate(1.0); // アニメーションを実行

    // アニメーション完了後、isLoadingをfalseにする
    setTimeout(() => {
      setIsLoading(false);
    }, 1000); // アニメーション時間と同じか、少し長めに設定

  }, []);

  return (
    <div className="bg-gray-200 font-sans">
      {isLoading && (
        <div id="loader" className="fixed top-0 left-0 w-full h-full bg-gray-100 flex justify-center items-center flex-col z-50">
          <img src="image/loading.gif" alt="ローディング中" className="w-24" />
          <div ref={splashTextRef} className="mt-4 text-xl text-gray-600">Now loading...</div>
        </div>
      )}

      {/* isLoadingがfalseの時だけコンテンツを表示 */}
      {!isLoading && (
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          <div className="header text-center py-8">
            {/* ... コンテンツ  */}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
