import React, { useState, useEffect, useRef } from 'react';
import ProgressBar from 'progressbar.js';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const splashTextRef = useRef(null); // splash_text要素への参照を保持

  useEffect(() => {
    // ページ読み込み後にProgressBarを初期化
    const bar = new ProgressBar.Line(splashTextRef.current, {
      // ... (progressbar.js の設定)
    });

    bar.animate(1.0, () => {
      setIsLoading(false);
    });
  }, []); // 

  return (
    <div className="bg-gray-200 font-sans">
      {isLoading && (
        <div id="loader" className="fixed top-0 left-0 w-full h-full bg-gray-100 flex justify-center items-center flex-col z-50">
          <img src="image/loading.gif" alt="ローディング中" className="w-24" />
          <div ref={splashTextRef} className="mt-4 text-xl text-gray-600">Now loading...</div>
        </div>
      )}

      {/* コンテンツ */}
      {!isLoading && (
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          {/* ... (コンテンツ) */}
        </div>
      )}
    </div>
  );
}

export default App;
